import React, { useState, useContext } from "react";
import { useHistory } from "react-router";
import {
    TextField,
    Button,
    styled,
    Switch,
    Box,
    Avatar,
    Link,
    Grid,
    Paper,
    Typography,
    FormHelperText,
} from "@mui/material";
import { ThemeProvider, makeStyles } from "@material-ui/core";
import theme from "../../ui/Theme";
import { LockOutlined } from "@mui/icons-material";
import axios from "axios";
import { AppContext } from "../../../AppContext";
import { useTheme } from "@mui/material/styles";

const initialValues = {
    email: "",
    password: "",
    conformPassword: "",
    userType: "developer",
    userName: "",
    college: "",
    graduationYear: "",
    fullName: "",
    contact: "",
    address: "",
};

const MaterialUISwitch = styled(Switch)(({ theme }) => ({
    width: 62,
    height: 34,
    padding: 7,
    "& .MuiSwitch-switchBase": {
        margin: 1,
        padding: 0,
        transform: "translateX(6px)",
        "&.Mui-checked": {
            color: "#fff",
            transform: "translateX(22px)",
            "& .MuiSwitch-thumb:before": {
                backgroundImage: `url('data:image/svg+xml;base64,')`,
            },
            "& + .MuiSwitch-track": {
                opacity: 1,
                backgroundColor:
                    theme.palette.mode === "dark" ? "#8796A5" : "#aab4be",
            },
        },
    },
    "& .MuiSwitch-thumb": {
        backgroundColor: theme.palette.mode === "dark" ? "#003892" : "#001e3c",
        width: 32,
        height: 32,
        "&:before": {
            content: "''",
            position: "absolute",
            width: "100%",
            height: "100%",
            left: 0,
            top: 0,
            backgroundRepeat: "no-repeat",
            backgroundPosition: "center",
            backgroundImage: `url('data:image/svg+xml;base64,')`,
        },
    },
    "& .MuiSwitch-track": {
        opacity: 1,
        backgroundColor: theme.palette.mode === "dark" ? "#8796A5" : "#aab4be",
        borderRadius: 20 / 2,
    },
}));

const useStyles = makeStyles({
    root: {
        "& .MuiFormControl-root": {
            width: "100%",
            marginTop: theme.spacing(1),
            marginBottom: theme.spacing(1),
        },
        "& .MuiButton-root": {
            width: "40%",
            marginTop: theme.spacing(1),
            marginBottom: theme.spacing(1),
        },
    },
    pageContent: {
        margin: theme.spacing(3),
        padding: theme.spacing(3),
        width: "35%",
        [theme.breakpoints.down('md')]: {
            width: "40%"
        },
        [theme.breakpoints.down('sm')]: {
            width: "90%"
        },
        margin: "auto",
        marginTop: "50px",
        marginBottom: "50px",
    },
    formLink: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
    },
    mainContainer: {
        display: "grid",
        minHeight: "100vh",
        alignContent: "center",
        
        backgroundColor: theme.palette.common.lightMainGreenColor,
    },
    formButton: {
        marginTop: theme.spacing(2),
        marginBottom: theme.spacing(1),
    },
    errorMessage: {
        margin: "0px",
    },
});

const SignUp = (props) => {
    const [values, setValues] = useState(initialValues);
    const [errors, setErrors] = useState({});
    const [isFormValid, setIsFormValid] = useState(false);

    const classes = useStyles();
    const history = useHistory();
    const { setShowBanner } = useContext(AppContext);

    const validateForm = (name, fieldValue) => {
        const fieldErrors = [];
        const hErrors = { ...errors };

        const Passval=/^(?=.*\d)(?=.*[A-Z])(?=.[a-z])(?=.*[a-z])(?=.*[a-zA-Z!#$@^%&?."])[a-zA-Z0-9!#$@^%&]{8,20}$/;

        const re =
            /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
        if (name === "email" && !re.test(fieldValue.toLowerCase())) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    Invalid Email
                </p>
            );
        }

        if (name === "userName" && fieldValue.length < 3) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    MinLength should be 3
                </p>
            );
        }
 
        if (name === "fullName" && fieldValue.length < 8) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    MinLength should be 8
                </p>
            );
        }

        if (name === "password" && !Passval.test(fieldValue)) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    MinLength should be 8 characters,atleast one uppercase , one lowercase , one number and one special Character
                </p>
            );
        }

        // if (name === "password" && fieldValue.length < 8) {
        //     fieldErrors.push(
        //         <p className={classes.errorMessage} name={name}>
        //             MinLength should be 8
        //         </p>
        //     );
        // }

        if (name === "conformPassword" && fieldValue !== values.password) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    Password does not match
                </p>
            );
        }

        if (name === "college" && fieldValue.length < 2) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    Please add valid College name
                </p>
            );
        }

        if (
            name === "graduationYear" &&
            (parseInt(fieldValue) < 2000 || parseInt(fieldValue) > 2026)
        ) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    Graduation Year should be between 2000 to 2026
                </p>
            );
        }

        if (name === "contact" && fieldValue.toString().length < 10) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    MinLength should be 10
                </p>
            );
        }

        if (name === "address" && fieldValue.length < 10) {
            fieldErrors.push(
                <p className={classes.errorMessage} name={name}>
                    Invalid address
                </p>
            );
        }

        return {
            ...hErrors,
            [name]: fieldErrors,
        };
    };

    const checkFormValidation = (formErrors) => {
        let valid = 1;
        // let tempErrors = {};

        for (const [key, value] of Object.entries(formErrors)) {
            if (value.length) {
                valid = 0;
                break;
            }
        }

        return valid;
    };

    const getHelperText = (name) => {
        if (errors[name] && errors[name].length) {
            return errors[name];
        }
    };

    const handleInputChange = (e) => {
        let { name, value } = e.target;
        
        const inputErrors = {
            ...errors,
            ...validateForm(name, value),
        };

        setValues({
            ...values,
            [name]: value,
        });

        setErrors({
            ...inputErrors,
        });

        setIsFormValid(checkFormValidation(inputErrors));
    };

    const handleToggleSwitch = (e) => {
        let { name, checked } = e.target;
        // changeFields();
        if (checked) {
            setValues({
                ...values,
                [name]: "organization",
            });
        } else {
            setValues({
                ...values,
                [name]: "developer",
            });
        }
    };

    const handleAfterFormResponse = () => {
        setTimeout(() => {
            setShowBanner(null);
        }, 4000);
    };

    // @TODO - Determine if the user Already exists | If so, send them to the dashboard

    // Handle Form Submit
    const handleSubmit = (e) => {
        e.preventDefault();

        console.log("I'm here");
        // After succesful validation
        try {
            axios
                .post("http://localhost:3001/signup", values)
                .then((response) => {
                    if (response.data.message === "You are registered,You can login now.") {
                        setShowBanner({
                            apiSuccessResponse: "Account created succesfully!",
                        });

                        console.log("User Created Succesfully");
                        return history.push("/auth/signin");
                    } else {
                        setShowBanner({
                            apiErrorResponse: response.data.warning,
                        });
                        console.log("Can't create user. Please try again!");
                    }
                })
                .catch((err) => {
                    //
                    setShowBanner({ apiErrorResponse: err.data });
                    console.log(
                        "Error while performing network request",
                        err.data
                    );
                });
        } catch (err) {
            setShowBanner({
                apiErrorResponse: "Some error occured. Please try again!",
            });
        } finally {
            handleAfterFormResponse();
        }
    };

    return (
        <ThemeProvider theme={theme}>
            <Grid className={classes.mainContainer} xs={12} sm={12} md={12}>
                <Paper className={classes.pageContent}>
                    <center>
                        <Avatar
                            style={{
                                backgroundColor: `${theme.palette.primary.main}`,
                            }}
                        >
                            <LockOutlined />
                        </Avatar>
                        <h2
                            style={{ color: `${theme.palette.secondary.main}` }}
                        >
                            Sign Up
                        </h2>
                    </center>
                    <form
                        className={classes.root}
                        elevaton={10}
                        onSubmit={handleSubmit}
                        method="POST"
                    >
                        <Grid container>
                            <Grid item xs={12} sm={12} md={12}>
                            <Box>
                                    <TextField
                                        variant="outlined"
                                        type="text"
                                        name="userName"
                                        label="User Name"
                                        value={values.userName}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <FormHelperText
                                        component="div"
                                        error={errors && errors.length > 0}
                                        style={{
                                            paddingLeft: "8px",
                                            boxSizing: "border-box",
                                            color: "red",
                                        }}
                                    >
                                        {getHelperText("userName")}
                                    </FormHelperText>
                                </Box>
                                <Box>
                                    <TextField
                                        variant="outlined"
                                        type="text"
                                        name="fullName"
                                        label="Full Name"
                                        value={values.fullName}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <FormHelperText
                                        component="div"
                                        error={errors && errors.length > 0}
                                        style={{
                                            paddingLeft: "8px",
                                            boxSizing: "border-box",
                                            color: "red",
                                        }}
                                    >
                                        {getHelperText("fullName")}
                                    </FormHelperText>
                                </Box>
                                <Box>
                                    <TextField
                                        variant="outlined"
                                        type="email"
                                        name="email"
                                        label="Email"
                                        value={values.email}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <FormHelperText
                                        component="div"
                                        error={errors && errors.length > 0}
                                        style={{
                                            paddingLeft: "8px",
                                            boxSizing: "border-box",
                                            color: "red",
                                        }}
                                    >
                                        {getHelperText("email")}
                                    </FormHelperText>
                                </Box>
                                
                                
                                <Box>
                                    <TextField
                                        variant="outlined"
                                        type="password"
                                        name="password"
                                        label="Password"
                                        value={values.password}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <FormHelperText
                                        component="div"
                                        error={errors && errors.length > 0}
                                        style={{
                                            paddingLeft: "8px",
                                            boxSizing: "border-box",
                                            color: "red",
                                        }}
                                    >
                                        {getHelperText("password")}
                                    </FormHelperText>
                                </Box>
                                <Box>
                                    <TextField
                                        variant="outlined"
                                        type="password"
                                        name="conformPassword"
                                        label="Conform Password"
                                        value={values.conformPassword}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <FormHelperText
                                        component="div"
                                        error={errors && errors.length > 0}
                                        style={{
                                            paddingLeft: "8px",
                                            boxSizing: "border-box",
                                            color: "red",
                                        }}
                                    >
                                        {getHelperText("conformPassword")}
                                    </FormHelperText>
                                </Box>
                                <Box>
                                    <Typography variant="button">
                                        <label>Student</label>
                                        <MaterialUISwitch
                                            sx={{ m: 1 }}
                                            onChange={handleToggleSwitch}
                                            name="userType"
                                        />
                                        <label>Organization</label>
                                    </Typography>
                                </Box>

                                {values.userType === "developer" ? (
                                    <>
                                        <Box>
                                            <TextField
                                                variant="outlined"
                                                type="text"
                                                name="college"
                                                label="College"
                                                value={values.college}
                                                onChange={handleInputChange}
                                                required
                                            />
                                            <FormHelperText
                                                component="div"
                                                error={
                                                    errors && errors.length > 0
                                                }
                                                style={{
                                                    paddingLeft: "8px",
                                                    boxSizing: "border-box",
                                                    color: "red",
                                                }}
                                            >
                                                {getHelperText("college")}
                                            </FormHelperText>
                                        </Box>
                                        <Box>
                                            <TextField
                                                variant="outlined"
                                                type="number"
                                                name="graduationYear"
                                                label="Graduation Year"
                                                value={values.graduationYear}
                                                onChange={handleInputChange}
                                                required
                                            />
                                            <FormHelperText
                                                component="div"
                                                error={
                                                    errors && errors.length > 0
                                                }
                                                style={{
                                                    paddingLeft: "8px",
                                                    boxSizing: "border-box",
                                                    color: "red",
                                                }}
                                            >
                                                {getHelperText(
                                                    "graduationYear"
                                                )}
                                            </FormHelperText>
                                        </Box>
                                    </>
                                ) : (
                                    <>
                                        <Box>
                                            <TextField
                                                variant="outlined"
                                                type="tel"
                                                name="contact"
                                                label="Contact Details"
                                                value={values.contact}
                                                onChange={handleInputChange}
                                                required
                                            />
                                            <FormHelperText
                                                component="div"
                                                error={
                                                    errors && errors.length > 0
                                                }
                                                style={{
                                                    paddingLeft: "8px",
                                                    boxSizing: "border-box",
                                                    color: "red",
                                                }}
                                            >
                                                {getHelperText("contact")}
                                            </FormHelperText>
                                        </Box>
                                        <Box>
                                            <TextField
                                                variant="outlined"
                                                type="text"
                                                name="address"
                                                label="Address"
                                                value={values.address}
                                                onChange={handleInputChange}
                                                required
                                            />
                                            <FormHelperText
                                                component="div"
                                                error={
                                                    errors && errors.length > 0
                                                }
                                                style={{
                                                    paddingLeft: "8px",
                                                    boxSizing: "border-box",
                                                    color: "red",
                                                }}
                                            >
                                                {getHelperText("address")}
                                            </FormHelperText>
                                        </Box>
                                    </>
                                )}

                                <center>
                                    <Box>
                                        <Button
                                            variant="contained"
                                            style={{
                                                backgroundColor: `${theme.palette.secondary.main}`,
                                                color: `${theme.palette.common.ternaryColor}`,
                                            }}
                                            type="Submit"
                                            disabled={!isFormValid}
                                        >
                                            Submit
                                        </Button>
                                    </Box>
                                    <Box>
                                        <Typography variant="body2">
                                            <Link href="http://localhost:3000/auth/signin">
                                                <b
                                                    style={{
                                                        color: `${theme.palette.common.darkGreen}`,
                                                    }}
                                                >
                                                    Already have an account?
                                                    Sign In Here
                                                </b>
                                            </Link>
                                        </Typography>
                                    </Box>
                                </center>

                            </Grid>
                        </Grid>
                    </form>
                </Paper>
            </Grid>
        </ThemeProvider>
    );
};

export default SignUp;
